import { createSlice, configureStore } from '@reduxjs/toolkit';

const initialState = { counter: 0, show: true };

//דרך עדכנית לצור 'סטט' חדש
const counterSlice = createSlice({
    name: "counter",
    initialState,
    reducers: {
        increment(state, action) {
            state.counter = state.counter + action.payload;
            state.show = true;
        },
        decrement(state) {
            state.counter--;
            state.show = true;
        },
        toggle(state) {
            state.show = !state.show;
        },
    }
})


const authSlice = createSlice({
    name: 'auth',
    initialState: { isAuth: false },
    reducers: {
        login(state) {
            state.isAuth = true;
        },
        logout(state) {
            state.isAuth = false;
        }
    }
})


const store = configureStore({
    // reducer: counterSlice.reducer
    reducer: {
        counter: counterSlice.reducer,
        auth: authSlice.reducer
    },
});


export default store;
export const counterActions = counterSlice.actions; //עכשיו האקשן נוצר עבורנו
export const authActions = authSlice.actions; //עכשיו האקשן נוצר עבורנו
//counterAction.increment/decrement/toggle - ככה קוראים לאקשן מעכשיו


