import { createStore } from 'redux';

const conuterReducer = (state = { counter: 0, show: true }, action) => {
    switch (action.type) {
        case "increment":
            return {
                ...state,
                counter: state.counter + action.payload
            };
        case 'decrement':
            return {
                ...state,
                counter: state.counter - 1
            };
        case "toggle":
            return {
                ...state,
                toggle: !state.show
            };
        default:
            return state
    }
}


const store = createStore(conuterReducer);


export default store;
